mapper.select:
select *
from users
where user_id = #{userId} and pw = #{pw}

select count(*)
from users
where user_id = #{userId}

select pw
from users
where pw = #{pw}

select count(*)
from users
where nickname = #{nickname}

select count(*)
from users
where phone_num = #{phoneNum}

select count(*)
from users
where email = #{email}
